      `${prefix}/alice/settings`,
    ]);

    for (const item of listResult.data) {
      assertEquals(item.uri.includes("alice"), true);
      assertEquals(item.uri.includes("bob"), false, "Should not include bob");
    }
  }

  await client.cleanup();
});

